import React from 'react'
import ReactDOM from 'react-dom'

const Header = (props) => {
  return(
    <div>
      <h1>{props.course}</h1>
    </div>
  )
}

const Content = (props) => {
  return(
    <div>
      <Part part={props.name} excercises={props.number}/>
      <Part part={props.name} excercises={props.number}/>
      <Part part={props.name} excercises={props.number}/>
    </div>
  )
}

const App = () => {
  const course = 'Half Stack application development'
  const part1 = 'Fundamentals of React'
  const exercises1 = 10
  const part2 = 'Using props to pass data'
  const exercises2 = 7
  const part3 = 'State of a component'
  const exercises3 = 14

  return (
    <div>
      <Header course={course} />
      <Content course={part1} excercises={excercises1}/>
      <Content course={part2} excercises={excercises2}/>
      <Content course={part3} excercises={excercises3}/>
    </div>
  )
}

ReactDOM.render(<App />, document.getElementById('root'))
