import React from 'react'
import ReactDOM from 'react-dom'

const Header = (props) => {
  return(
    <div>
      <h1>{props.course}</h1>
    </div>
  )
}

const Content = (props) => {
  return(
    <div>
      <p>
      {props.content.map(function(d, idx){
         return (<li key={idx}>{d.part}</li>)
       })}
      </p>
    </div>
  )
}

const Total = (props) => {
  return(
    <div>
      <p>Number of excercises {props.total}</p>
    </div>
  )
}

const App = () => {
  const course = 'Half Stack application development'

  const contentList = [
    {
      part: 'Fundamentals of React',
      excercises: 10
    },
    {
      part2: 'Using props to pass data',
      excercises: 7
    },
    {
      part3: 'State of a component',
      excercises: 14
    }
  ];

  const sum = function(items, prop) {
    return items.reduce(function(a, b) {
      return a+ + b[prop];
    }, 0);
  }

  const total = sum(contentList, 'excercises')

  return (
    <div>
      <Header course={course} />
      <Content content={contentList}/>
      <Total total={total}/>
    </div>
  )
}

ReactDOM.render(<App />, document.getElementById('root'))
