import React from 'react'
import ReactDOM from 'react-dom'

const Header = (props) => {
  return(
    <div>
      <h1>{props.course}</h1>
    </div>
  )
}

const Course = (props) => {
  return(
    <div>
      <p>
      {
        props.parts.map(function(content, idx){
         return (<li key={idx}>{content.name}  {content.excercises}</li>)
        })
       }
      </p>
    </div>
  )
}

const Total = (props) => {
  return(
    <div>
      <p><strong>Number of excercises {props.total}</strong></p>
    </div>
  )
}

const App = () => {

  const course = {
    id: 1,
    name: 'Half Stack application development',
    parts: [
      {
        name: 'Fundamentals of React',
        excercises: 10,
        id: 1
      },
      {
        name: 'Using props to pass data',
        excercises: 7,
        id: 2
      },
      {
        name: 'State of a component',
        excercises: 14,
        id: 3
      }
    ]
  };

  const sum = function(items, prop) {
    return items.reduce(function(a, b) {
      return a+ + b[prop];
    }, 0);
  }

  const total = sum(course.parts, 'excercises');

  return (
    <div>
      <Header course={course.name} />
      <Course parts={course.parts}/>
      <Total total={total}/>
    </div>
  )
}

ReactDOM.render(<App />, document.getElementById('root'))
