import React from 'react'
import ReactDOM from 'react-dom'

const Header = (props) => {
  return(
    <div>
      <h1>{props.course}</h1>
    </div>
  )
}

const Content = (props) => {
    props.forEach(function(elem) {
      return(
        <div>
          <p>
           {elem.part} {elem.excercises}
         </p>
        </div>
      )
    })
}

const Total = (props) => {
  return(
    <div>
      <p>Number of excercises {props.total}</p>
    </div>
  )
}

const App = () => {
  const course = 'Half Stack application development'

  const contentList = [
    {
      part: 'Fundamentals of React',
      excercises: 10
    },
    {
      part2: 'Using props to pass data',
      excercises: 7
    },
    {
      part3: 'State of a component',
      excercises: 14
    }
  ];

  const sum = function(items, prop) {
    return items.reduce(function(a, b) {
      return a+ + b[prop];
    }, 0);
  }

  const total = sum(contentList, 'exercises')

  return (
    <div>
      <Header course={course} />
      <Content contentList/>
      <Total total={part1 + part2 + part3}/>
    </div>
  )
}

ReactDOM.render(<App />, document.getElementById('root'))
