import React from 'react'
import ReactDOM from 'react-dom'

const Header = (props) => {
  return(
    <div>
      <h1>{props.course}</h1>
    </div>
  )
}

const Content = (props) => {
  console.log(props)
  return(
    props.forEach(function(elem) {
      <div>
        <p>
          {elem.part} {elem.excercises}
        </p>
      </div>
    })
  )
}

const Total = (props) => {
  return(
    <div>
      <p>Number of excercises {props.total}</p>
    </div>
  )
}

const App = () => {
  const course = 'Half Stack application development'
  const part1 = 'Fundamentals of React'
  const exercises1 = 10
  const part2 = 'Using props to pass data'
  const exercises2 = 7
  const part3 = 'State of a component'
  const exercises3 = 14

  const contentList = [
    {
      part: part1,
      excercises: excercises1
    },
    {
      part2: part2,
      excercises: excercises2
    },
    {
      part3: part3,
      excercises: excercises3
    }
  ];

  return (
    <div>
      <Header course={course} />
      <Content contentList/>
      <Total total={part1 + part2 + part3}/>
    </div>
  )
}

ReactDOM.render(<App />, document.getElementById('root'))
