import React, { useState } from 'react'
import ReactDOM from 'react-dom'

import IconLabelButtons from './iconbuttonlib'
import FavoriteIcon from '@material-ui/icons/Check';
import ThumbsUp from '@material-ui/icons/Check';
import ThumbsDown from '@material-ui/icons/Check';
import ThumbsUpDown from '@material-ui/icons/Check';

const App = () => {
  const [good, setGood] = useState(0);
  const [neutral, setNeutral] = useState(0);
  const [bad, setBad] = useState(0);

  const handleGoodClick = () => setGood(good + 1);
  const handleNeutralClick = () => setGood(neutral + 1);
  const handleBadClick = () => setGood(bad + 1);

  return (
    <div>
      <h1>give feedback</h1>
      {IconLabelButtons("contained", "primary", "good", <ThumbsUp/>, {handleGoodClick})}
      {IconLabelButtons("contained", "primary", "neutral", <ThumbsUpDown/>, {handleNeutralClick})}
      {IconLabelButtons("contained", "primary", "bad", <ThumbsUpDown/>, {handleBadClick})}
    </div>
  )
}

ReactDOM.render(
  <App />, 
  document.getElementById('root')
)
