import React, { useState } from 'react'
import ReactDOM from 'react-dom'

import IconLabelButtons from './iconbuttonlib'
import {ThumbsUp, ThumbsDown, ThumbsUpDown} from '@material-ui/icons/*';

const DisplayStatistic = (props) => {
  return(
    <div>
      <p>{props.statistic} : {props.amount}</p>
    </div>
  )
}

const App = () => {
  const [good, setGood] = useState(0);
  const [neutral, setNeutral] = useState(0);
  const [bad, setBad] = useState(0);

  const handleGoodClick = () => setGood(good + 1);
  const handleNeutralClick = () => setNeutral(neutral + 1);
  const handleBadClick = () => setBad(bad + 1);

  return (
    <div>
      <h1>give feedback</h1>
      {IconLabelButtons("contained", "primary", "good", <ThumbsUp/>, handleGoodClick)}
      {IconLabelButtons("contained", "primary", "neutral", <ThumbsUpDown/>, handleNeutralClick)}
      {IconLabelButtons("contained", "primary", "bad", <ThumbsDown/>, handleBadClick)}
      <h1>Statistics</h1>
      <DisplayStatistic statistic={"good"} amount={good}/>
      <DisplayStatistic statistic={"neutral"} amount={neutral}/>
      <DisplayStatistic statistic={"bad"} amount={bad}/>
    </div>
  )
}

ReactDOM.render(
  <App />, 
  document.getElementById('root')
)
