import './index.css';

import React, { useState, useEffect, Fragment } from 'react'
import axios from 'axios'

import Input from '@material-ui/core/Input';

const Search = (props) => {

  const [searchTerm, setSearchTerm] = useState('');
  let searchResults = [];

  const getSearchResults = (countries, searchTerm) => {
    return countries.filter((country) => {
      return country.name.toUpperCase().includes(searchTerm.toUpperCase());
    });
  }

  const updateSearchInput = (event) => {
    setSearchTerm(event.target.value);
    searchResults = getSearchResults(props.countries, searchTerm);
    console.log(searchResults);
  }

  return(
    <Fragment>
      <div>
        <Input name='Search' value={searchTerm} type="text" placeholder='enter a country' onInput={updateSearchInput}/>
      </div>
     <div>
        {searchResults.map((country) => {
          return (
            <li 
              key={country.numericCode}>{country.name}
            </li>)
        })}
      </div>
    </Fragment>
  )
}

const App = () => {

  const [countries, setCountries] = useState([]);

  const hook = () => {
    axios
      .get('https://restcountries.eu/rest/v2/all')
      .then(response => {
        console.log('promise fulfilled', response.data);
        setCountries(response.data);
      })
  }

  useEffect(hook, []);

  return (
    <div>
      <Search countries={countries} />
    </div>
  )
}

export default App
