import './index.css';

import React, { useState, useEffect, Fragment } from 'react'
import axios from 'axios'

import Input from '@material-ui/core/Input';

const Search = (props) => {

  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);

  const getSearchResults = (countries, searchTerm) => {
    return countries.filter((country) => {
      return country.name.toUpperCase().includes(searchTerm.toUpperCase());
    });
  }

  const updateSearchInput = (event) => {
    setSearchTerm(event.target.value);
    setSearchResults(getSearchResults(props.countries, searchTerm));
  }

  const showResults = (results) => {
    if(results.length > 10) {
      return <p>Too many matches, specify another filter</p>;
    } else if(results.length > 1 && results.length <= 10) {
      return results.map((country) => {
        return (
          <li 
            key={country.numericCode}>{country.name}
          </li>)
      })
    } else if(results.length === 1) {
      const country = results[0];
      console.log(country);
      return(
        <Fragment>
          <h1>{country.name}</h1>
          <p>capital {country.capital}</p>
          <p>population {country.population}</p>
          <h2>languages</h2>
          {country.languages.map((language) => {
            return (
              <li 
                key={language.iso639_2}>{language.name}
              </li>)
            })}
          <img src={country.flag} alt="Flag" />;
        </Fragment>
      )
    }
  }

  return(
    <Fragment>
      <div>
        <Input name='Search' value={searchTerm} type="text" placeholder='enter a country' onInput={updateSearchInput}/>
      </div>
    <br></br>
     <div>
       {showResults(searchResults)}
     </div>
    </Fragment>
  )
}

const App = () => {

  const [countries, setCountries] = useState([]);

  const hook = () => {
    axios
      .get('https://restcountries.eu/rest/v2/all')
      .then(response => {
        console.log('promise fulfilled', response.data);
        setCountries(response.data);
      })
  }

  useEffect(hook, []);

  return (
    <div>
      <Search countries={countries} />
    </div>
  )
}

export default App
