import './index.css';

import React, { useState } from 'react'

import IconLabelButtons from './iconbuttonlib';
import AddIcon from '@material-ui/icons/Add';
import Input from '@material-ui/core/Input';

const App = () => {

  let searchTerm = '';

  const [ persons, setPersons ] = useState([
    { name: 'Arto Hellas', number: '040-123456', id : '1' },
    { name: 'Ada Lovelace', number: '39-44-5323523', id : '2' },
    { name: 'Dan Abramov', number: '12-43-234345', id : '3' },
    { name: 'Mary Poppendieck', number: '39-23-6423122', id : '4' }
  ]);
  const [ searchResults, setSearchResults ] = useState([...persons]);
  const [ newName, setNewName ] = useState('');

  const [ newNumber, setNewNumber ] = useState('');

  const [ nameError, setNameError ] = useState({error : false, message : ''});
  const [ numberError, setNumberError ] = useState({error : false, message : ''});

  function create_UUID(){
    var dt = new Date().getTime();
    var uuid = 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
        var r = (dt + Math.random()*16)%16 | 0;
        dt = Math.floor(dt/16);
        return (c==='x' ? r :(r&0x3|0x8)).toString(16);
    });
    return uuid;
  }

  const updateSearchInput = (event) => {
    searchTerm = event.target.value;
    console.log('searchTerm: ', searchTerm);
    let results = persons.filter((person) => person.name.toLowerCase().startsWith(searchTerm.toLowerCase()));
    console.log('results: ', results);
    setSearchResults(results);
  }
  const updateNameInput = (event) => setNewName(event.target.value);
  const updateNumberInput = (event) => setNewNumber(event.target.value);

  const validateForm = () => {
    let nameIsEmpty = !newName;
    let nameAlreadyExists = persons.filter((person) => {
      return person.name === newName
    }).length > 0;
    let nameMessage = nameIsEmpty ? 'Field is required' : nameAlreadyExists ? `${newName} is already added to phonebook` : '';
    let isNameValid = !(nameIsEmpty || nameAlreadyExists);
    setNameError({error : !isNameValid, message : nameMessage});

    let numberIsEmpty = !newNumber;
    let numberMessage = numberIsEmpty ? 'Field is required' : '';
    let isNumberValid = !(numberIsEmpty);
    setNumberError({error : !isNumberValid, message : numberMessage});
    return (isNameValid && isNumberValid);
  }

  const handleSubmit = (event) => {
    event.preventDefault();
    if(validateForm()) {
        setNewName(newName);
        setNewNumber(newNumber);
        setPersons([...persons].concat({ name : newName, number : newNumber, id : create_UUID() }));
        setNewName('');
        setNewNumber('');
    }
  }

  return (
    <div>
      <h2>Phonebook</h2>
      <div>
          <Input name='Search' type="text" placeholder='enter a searchterm' onInput={updateSearchInput}/>
      </div>
      <br></br>
      <form>
        <div>
          <Input error={nameError.error} name='Name' type="text" value={newName} placeholder='name' onChange={updateNameInput}/>
          <div>
            <span className='error'>{nameError.message}</span>
          </div>
        </div>
        <div>
          <Input error={numberError.error} name='Number' type="text" value={newNumber} placeholder='number' onChange={updateNumberInput}/>
          <div>
            <span className='error'>{numberError.message}</span>
          </div>
        </div>
        <div>
        {IconLabelButtons("contained", "submit", "primary", "add", <AddIcon/>, handleSubmit)}
        </div>
      </form>
      <h2>Numbers</h2>
      <p>
      {searchResults.map((person) => {
         return (
         <li 
            key={person.id}>{person.name} {person.number}
         </li>)
      })}
      </p>
    </div>
  )
}

export default App
