import './index.css';

import React, { useState } from 'react'

import IconLabelButtons from './iconbuttonlib';
import AddIcon from '@material-ui/icons/Add';
import Input from '@material-ui/core/Input';

const App = () => {
  const [ persons, setPersons ] = useState([
    { name: 'Arto Hellas', id: 0 }
  ]);
  const [ newName, setNewName ] = useState('');

  const [ nameError, setNameError ] = useState({error : false, message : ''});

  function create_UUID(){
    var dt = new Date().getTime();
    var uuid = 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
        var r = (dt + Math.random()*16)%16 | 0;
        dt = Math.floor(dt/16);
        return (c==='x' ? r :(r&0x3|0x8)).toString(16);
    });
    return uuid;
  }

  const updateNameInput = (event) => setNewName(event.target.value);

  const validateForm = () => {
    let nameIsEmpty = !newName;
    let nameAlreadyExists = persons.filter((person) => {
      return person.name === newName
    }).length > 0;
    let message = nameIsEmpty ? 'Field is required' : nameAlreadyExists ? 'Name already exists' : '';
    let isValid = !(nameIsEmpty || nameAlreadyExists);
    return {isValid : isValid, message : message};
  }

  const handleSubmit = (event) => {
    event.preventDefault();
    const validation = validateForm();
    setNameError({error : validation.isValid, message : validation.message})
    if(validation.isValid) {
        setNewName(newName);
        setPersons([...persons].concat({ name : newName, id : create_UUID() }));
        setNewName('');
    }
  }

  return (
    <div>
      <h2>Phonebook</h2>
      <form>
        <div>
          <Input error={nameError.error} name='Name' type="text" value={newName} placeholder='name' onChange={updateNameInput}/>
          <div>
            <span className='error'>{nameError.message}</span>
          </div>
        </div>
        <div>
        {IconLabelButtons("contained", "submit", "primary", "add", <AddIcon/>, handleSubmit)}
        </div>
      </form>
      <h2>Numbers</h2>
      <p>
      {persons.map(function(person){
         return (
         <li 
            key={person.id}>{person.name}
         </li>)
      })}
      </p>
    </div>
  )
}

export default App
