import './index.css';

import React, { useState } from 'react'

import IconLabelButtons from './iconbuttonlib';
import AddIcon from '@material-ui/icons/Add';
import Input from '@material-ui/core/Input';

const App = () => {
  const [ persons, setPersons ] = useState([
    { name: 'Arto Hellas', id: 0 }
  ]) 
  const [ newName, setNewName ] = useState('')

  function create_UUID(){
    var dt = new Date().getTime();
    var uuid = 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
        var r = (dt + Math.random()*16)%16 | 0;
        dt = Math.floor(dt/16);
        return (c==='x' ? r :(r&0x3|0x8)).toString(16);
    });
    return uuid;
  }

  const updateNameInput = (event) => setNewName(event.target.value);

  const handleSubmit = (event) => {
      event.preventDefault();
      setNewName(newName);
      setPersons([...persons].concat({ name : newName, id : create_UUID() }));
  }

  return (
    <div>
      <h2>Phonebook</h2>
      <form>
        <div>
          <Input name='Name' type="text" value={newName} placeholder='name' onChange={updateNameInput}/>
        </div>
        <div>
        {IconLabelButtons("contained", "submit", "primary", "add", <AddIcon/>, handleSubmit)}
        </div>
      </form>
      <h2>Numbers</h2>
      <p>
      {persons.map(function(person){
         return (
         <li 
            key={person.id}>{person.name}
         </li>)
      })}
      </p>
    </div>
  )
}

export default App
